mapper.select:
SELECT * FROM (SELECT A.*, @ROWNUM := @ROWNUM + 1 AS RN FROM (SELECT * FROM ADMIN_NOTICES WHERE admin_id = 'admin'
and notices_title like CONCAT('%', #{searchName},'%')
and (notices_title like CONCAT('%', #{searchName},'%') or notices_content like CONCAT('%', #{searchName},'%'))
and notices_content like CONCAT('%', #{searchName},'%')
ORDER BY ADMIN_KEY DESC)A, (SELECT @ROWNUM := 0) D
)B WHERE rn > (#{page}-1) * #{amount} AND rn <= #{page} * #{amount}

mapper.update:
update admin_notices set notices_count=
notices_count+1 where
admin_key=#{admin_key}

update admin_notices set

notices_content = #{notices_content},
notices_title = #{notices_title},
admin_id = #{admin_id},
filename=#{filename}
where admin_key = #{admin_key}
