mapper.select:
select * from movie


		where movie_gr = "액션" and movie_nt != "한국"
		order by movie_opendt desc limit 10;


		where movie_gr = "스릴러" and movie_nt != "한국"
		order by movie_opendt desc limit 10;


		where movie_gr = "로맨스" and movie_nt != "한국"
		order by movie_opendt desc limit 10;


select round(avg(sr_rate),1) 
from star_rate 
where movie_koficCd=#{movie_koficCd};
